package com.soywiz.korge.scene

import com.soywiz.kds.*
import com.soywiz.korim.bitmap.*
import com.soywiz.korim.font.*
import com.soywiz.korim.format.*
import com.soywiz.korio.util.*
import com.soywiz.korio.util.encoding.*

object DebugBitmapFont {
	val DEBUG_FONT_BYTES: ByteArray = "".fromBase64()
}

private var bmpFontOnce2 = AsyncOnce<BitmapFont>()

suspend fun getDebugBmpFontOnce() = bmpFontOnce2 {
	val tex = nativeImageFormatProvider.decode(DebugBitmapFont.DEBUG_FONT_BYTES).slice()
	val fntAdvance = 7
	val fntWidth = 8
	val fntHeight = 8

    val fntBlockX = 2
    val fntBlockY = 2
    val fntBlockWidth = 12
    val fntBlockHeight = 12

    BitmapFont(tex.bmp, fntHeight, fntHeight, fntHeight, (0 until 256).associateWith {
		val x = it % 16
		val y = it / 16
		BitmapFont.Glyph(
			it,
			tex.sliceWithSize(x * fntBlockWidth + fntBlockX, y * fntBlockHeight + fntBlockY, fntWidth, fntHeight),
			0,
			0,
			fntAdvance
		)
	}.toIntMap(), IntMap())
}
